import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import MyButton from '../../util/MyButton';
import AppLogo from '../../images/plantrsleaflogo.png';
import AppIcon from '../../images/plantrsicon.png';
import PostPlant from '../plant/PostPlant';

// Material UIs
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import withStyles from '@material-ui/core/styles/withStyles';

// Icons
import Notifications from '@material-ui/icons/Notifications';
import classes from '*.module.css';

const styles = (theme) => ({
    ...theme.spreadThis,
    logo: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block'
        }
    }
})

class Navbar extends Component {
    render() {
        const { authenticated } = this.props
        return (
            <AppBar>
                <Toolbar className="nav-container">

                    {authenticated ? (
                        <Fragment>
                            <Link to="/">
                                <MyButton tip="Home">

                                <picture >
                                    <source media="(max-width: 400px)" srcset={AppIcon} alt="plantrslogo"/>
                                    <img className={classes.logo} src={AppLogo} alt="plantrslogo"/>
                                </picture>


                                </MyButton>
                            </Link>
                            <PostPlant/>
                            <MyButton tip="Notifications">
                                <Notifications/>
                            </MyButton>
                        </Fragment>
                    ) : (
                        <Fragment>
                            <Button color="inherit" component={Link} to="/">
                            <img src={AppLogo} alt="plantrslogo"/>
                            </Button>
                            <Button color="inherit" component={Link} to="/login">Login</Button>
                        </Fragment>
                    )}
                </Toolbar>
            </AppBar>
        );
    }
}

Navbar.propTypes = {
    authenticated: PropTypes.bool.isRequired
}

const mapStateToProps = state => ({
    authenticated: state.user.authenticated
})

export default connect(mapStateToProps)(withStyles(styles)(Navbar));;
