import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';

// Material UIs
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';

const styles = {
    card: {
        display: 'flex'
    }
}

class Plant extends Component {
    render() {
        const { 
                classes, 
                plant : { 
                    caption, 
                    createDate, 
                    userImage, 
                    userHandle, 
                    plantId, 
                    plantName, 
                    imageUrl, 
                    kingdom, 
                    likeCount, 
                    commentCount 
                } 
            } = this.props
        return (
            <Card>
                <CardHeader
                    disableTypography={false}
                    avatar={
                        <Avatar aria-label="recipe" className={classes.avatar} src={userImage}>
                          P
                        </Avatar>
                      }
                    titleTypographyProps={
                        <Typography variant="h5" >{userHandle}</Typography>
                    }
                />
                <CardMedia image={imageUrl} title={plantName} />
                <CardContent>
                    <Typography variant="h5">{plantName}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p">{caption}</Typography>
                </CardContent>

            </Card>
        )
    }
}

export default withStyles(styles)(Plant);
