import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import Link from 'react-router-dom/Link';

// Material UIs
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';


const styles = {
    card: {
        display: 'flex'
    }
}

class Plant extends Component {
    render() {
        const { 
                classes, 
                plant : { 
                    caption, 
                    createDate, 
                    userImage, 
                    userHandle, 
                    plantId, 
                    plantName, 
                    plantImg, 
                    kingdom, 
                    likeCount, 
                    commentCount 
                } 
            } = this.props
        return (
            <Card>
                <CardHeader
                    avatar={<Avatar alt={userHandle} src={userImage} className={classes.avatar}/>}
                    className={classes.cardHeader}
                    title={<Link to={`/users/${userHandle}`} color="primary">{userHandle}</Link>}
                    // title={userHandle}

                    subheader={createDate}
                />
                <CardMedia className={classes.media} image={userImage} title="Plant image" />
                <CardContent>
                    <Typography variant="h5">{plantName}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p">{caption}</Typography>
                </CardContent>

            </Card>
        )
    }
}

export default withStyles(styles)(Plant);
