import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import MyButton from '../util/MyButton';

// Redux stuff
import { connect } from 'react-redux';
import { postPlant } from '../redux/actions/dataActions';

// MUI stuff
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import CircularProgress from '@material-ui/core/CircularProgress';

// Icons
import AddIcon from '@material-ui/icons/Add';
import CloseIcon from '@material-ui/icons/Close';

const styles = (theme) => ({
    ...theme.spreadThis,
    submitButton:{
        position: 'relative'
    },
    progressSpinner:{
        position: 'absolute'
    },
    closeButton:{
        position: 'absolute',
        left: '90%',
        top: '2%'
    }
})

class PostPlant extends Component{
    state = {
        open: false,
        plantName: '',
        phylum: '',
        klass: '',
        urder: '',
        family: '',
        genus: '',
        species: '',
        caption: '',
        errors: {}
    };
    static getDerivedStateFromProps(props) {
        if (props.UI.errors) {
          return {
            errors: props.UI.errors
          }
        }
        return null;
    };
    handleOpen = () => {
        this.setState({ open: true })
    };
    handleClose = () => {
        this.setState({ open: false })
    };
    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value})
    };
    handleSubmit =(event) => {
        event.preventDefault();
        this.props.postPlant({ plantName: this.state.plantName });
        this.props.postPlant({ caption: this.state.caption });
    }
    render(){
        const { errors } = this.state;
        const { classes, UI: { loading }} = this.props;
        return (
            <Fragment>
                <MyButton onClick={this.handleOpen} tip="Post a Plant!">
                    <AddIcon/>
                </MyButton>
                <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <MyButton tip="Close" onClick={this.handleClose} tipClassName={classes.closeButton}>
                        <CloseIcon/>
                    </MyButton>
                    <DialogTitle>Post a new plant</DialogTitle>
                    <DialogContent>
                        <form onSubmit={this.handleSubmit}>
                            <TextField
                                name="plantName"
                                type="text"
                                label="PLANT"
                                placeholder="Name of your plant"
                                error={errors.plantName ? true : false}
                                helperText={errors.plantName}
                                className={classes.textField}
                                onChange={this.handleChange}
                                fullWidth
                                />
                            <TextField
                                name="caption"
                                type="text"
                                label="DESCRIPTION"
                                multiline
                                rows="3"
                                placeholder="Describe your plant"
                                error={errors.caption ? true : false}
                                helperText={errors.caption}
                                className={classes.textField}
                                onChange={this.handleChange}
                                fullWidth
                                />
                                <Button type="submit" variant="contained" color="primary"
                                    className={classes.submitButton} disabled={loading}>
                                        Submit
                                        {loading && (
                                            <CircularProgress size={20} className={classes.progressSpinner}/>
                                        )}
                                    </Button>
                        </form>
                    </DialogContent>
                </Dialog>
            </Fragment>
        )
    }
}

PostPlant.propTypes = {
    postPlant: PropTypes.func.isRequired,
    UI: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
    UI: state.UI
})

export default connect(
    mapStateToProps, 
    { postPlant }
)(withStyles(styles)(PostPlant));
