import { SET_PLANTS, LIKE_PLANT, UNLIKE_PLANT, LOADING_DATA } from '../types';

const initialState = {
    plants: [],
    plant: {},
    loading: false
};

export default function(state = initialState, actions){
    switch(actions.type){
        case LOADING_DATA:
            return {
                ...state,
                loading: true
            }
        case SET_PLANTS:
            return {
                ...state,
                plants: action.payload,
                loading: false
            }
        case LIKE_PLANT:
        case UNLIKE_PLANT:
            let index = state.plants.findIndex((plant) => plant.plantId === action.payload.plantId);
            state.plants[index] = action.payload;
            return {
                ...state
            }
    }
}
