import React, { Component } from 'react';
import axios from 'axios';
import Grid from '@material-ui/core/Grid';

class home extends Component {
    state = {
        plants: null
    }
    componentDidMount(){
        axios
            .get('/plants')
            .then((res) => {
                console.log(res.data)
                this.setState({
                    plants: res.data
                })
            })
            .catch((err) => console.log(err));
    }
    render() {
        let recentPlantsMarkup = this.state.plants ? (
            this.state.plants.map((plant) => <p>{plant.commonName}</p>)
        ) : <p>Loading...</p>
        return (
            <Grid container spacing={16}>
                <Grid item sm={8} xs={12}>
                    {recentPlantsMarkup}
                </Grid>
                <Grid item sm={4} xs={12}>
                    <p>Profile...</p>
                </Grid>
            </Grid>
        )
    }
}

export default home;
