import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import MyButton from '../util/MyButton';
import dayjs from 'dayjs';
import { Link } from 'react-router-dom';
// MUI 
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import CircularProgress from '@material-ui/core/CircularProgress';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
// Icons
import CloseIcon from '@material-ui/icons/Close';
import UnfoldMore from '@material-ui/icons/UnfoldMore';
// Redux stuff
import { connect } from 'react-redux';
import { getPlant } from '../redux/actions/dataActions';


const styles = (theme) => ({
    ...theme.spreadThis,
    invisibleSeparator: {
        border: 'none',
        margin: 4
    },
    profileImage: {
        maxWidth: 50,
        height: 50,
        borderRadius: '50%',
        objectFit: 'cover'
    },
    plantImage: {
        maxWidth: '100%',
        width: 'auto',
        height: 'auto',
        borderRadius: '5%',
        objectFit: 'cover'
    },
    dialogContent: {
        padding: 20
    },
    closeButton: {
        position: 'absolute',
        left: '90%'
    }
})

class PlantDialog extends Component{
    state = {
        open: false
    }
    handleOpen = () => {
        this.setState({ open: true });
        this.props.getPlant(this.props.plantId);
    }
    handleClose = () => {
        this.setState({ open: false });
    }
    render(){
        const { 
            classes, 
            plant: { 
                plantId, 
                plantName, 
                createDate, 
                phylum, 
                klass, 
                family, 
                urder, 
                genus, 
                species, 
                plantImg, 
                caption, 
                userHandle, 
                userImage, 
                likeCount, 
                commentCount 
            }, 
            UI: { loading } 
        } = this.props;

        const dialogMarkup = loading ? (
            <CircularProgress size={200}/>
        ) : (
            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <img src={plantImg} alt={plantName} className={classes.plantImage}/>
                </Grid>
                <Grid item sm={6}>
                    <hr className={classes.invisibleSeparator}/>
                    <Typography variant="body1">
                        {plantName}
                    </Typography>
                    <Typography variant="body1">
                        {caption}
                    </Typography>
                    <Typography variant="body2">
                        Phylum: {phylum}
                    </Typography>
                    <Typography variant="body2">
                        Class: {klass}
                    </Typography>
                    <Typography variant="body2">
                        Family: {family}
                    </Typography>
                    <Typography variant="body2">
                        Order: {urder}
                    </Typography>
                    <Typography variant="body2">
                        Genus: {genus}
                    </Typography>
                    <Typography variant="body2">
                        Species: {species}
                    </Typography>
                </Grid>
            </Grid>
        )

        return (
            <Fragment>
                <MyButton onClick={this.handleOpen} tip="Expand plant" tipClassName={classes.expandButton}>
                    <UnfoldMore color="primary"/>
                </MyButton>
                <Dialog
                open={this.state.open}
                onClose={this.handleClose}
                fullWidth
                maxWidth="sm"
                >
                    <MyButton
                        tip="Close"
                        onClick={this.handleClose}
                        tipClassName={classes.closeButton}
                    >
                        <CloseIcon/>
                    </MyButton>
                    <DialogTitle>
                    <img src={userImage} alt={userHandle} className={classes.profileImage}/>
                        <Typography
                            component={Link}
                            color="primary"
                            variant="h5"
                            to={`/users/${userHandle}`}
                        >
                            @{userHandle}
                        </Typography>
                        <hr className={classes.invisibleSeparator}/>
                        <Typography variant="body2" color="textSecondary">
                            {dayjs(createDate).format('h:mm a, MMMM DD YYYYY')}
                        </Typography>
                    </DialogTitle>
                    <DialogContent className={classes.dialogContent}>
                        {dialogMarkup}
                    </DialogContent>
                </Dialog>
            </Fragment>

        )
    }

};

PlantDialog.propTypes = {
    getPlant: PropTypes.func.isRequired,
    plantId: PropTypes.string.isRequired,
    userHandle: PropTypes.string.isRequired,
    plant: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    plant: state.data.plant,
    UI: state.UI
});

const mapActionsToProps = {
    getPlant
};

export default connect(mapStateToProps, mapActionsToProps)(withStyles(styles)(PlantDialog))
