import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import { Link } from 'react-router-dom';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';

// Material UIs
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { lightGreen } from '@material-ui/core/colors';

const styles = {
    card: {
        marginBottom: 20,
    },
    content: {
        padding: 20,
        objectFit: 'cover',
        minWidth: 400,
    },
    media: {
        minWidth: 400,
        minHeight: 400,
        paddingTop: '56.25%', // 16:9
    },
    avatar: {
        backgroundColor: lightGreen[500],
      },
}

class Plant extends Component {
    render() {
        dayjs.extend(relativeTime)
        const { 
                classes, 
                plant : { 
                    caption, 
                    createDate, 
                    userImage, 
                    userHandle, 
                    plantId, 
                    plantName, 
                    plantImg, 
                    kingdom, 
                    phylum,
                    klass,
                    urder,
                    family,
                    genus,
                    species,
                    likeCount, 
                    commentCount 
                } 
            } = this.props
        return (
            <Card className={classes.card}>
                <CardContent className={classes.content}>
                    <Avatar alt={userHandle} src={userImage} />
                    <Typography
                         variant="body2" 
                         component={Link} 
                         to={`/users/${userHandle}`} 
                         color="primary">{userHandle}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p">{dayjs(createDate).fromNow()}</Typography>
                </CardContent>
                <CardMedia className={classes.media} image={plantImg} title={plantName} />

                <CardContent className={classes.content}>
                    <Typography variant="h5">{plantName}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Description: </strong>{caption}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Kingdom: </strong>{kingdom}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Phylum: </strong>{phylum}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Class: </strong>{klass}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Order: </strong> {urder}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Family: </strong>{family}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Genus: </strong>{genus}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Species: </strong>{species}</Typography>
                </CardContent>

            </Card>
        )
    }
}

export default withStyles(styles)(Plant);
