import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import { Link } from 'react-router-dom';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import PropTypes from 'prop-types';
import MyButton from '../util/MyButton';
import DeletePlant from './DeletePlant';


// Material UIs
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { lightGreen } from '@material-ui/core/colors';

// Icons
import ChatIcon from '@material-ui/icons/Chat';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorder from '@material-ui/icons/FavoriteBorder';

// Redux stuff
import { connect } from 'react-redux';
import { likePlant, unlikePlant } from '../redux/actions/dataActions';


const styles = {
    card: {
        position: 'relative',
        marginBottom: 20,
    },
    content: {
        padding: 20,
        objectFit: 'cover',
        minWidth: 400,
    },
    media: {
        Width: 300,
        Height: 300,
        paddingTop: '56.25%', // 16:9
    },
    avatar: {
        backgroundColor: lightGreen[500],
      },
}

class Plant extends Component {
    likedPlant = () => {
        if(this.props.user.likes && 
            this.props.user.likes.find(
                (like) => like.plantId === this.props.plant.plantId
            )
        )
            return true;
        else return false;
    };
    likePlant = () => {
        this.props.likePlant(this.props.plant.plantId);
    }
    unlikePlant = () => {
        this.props.unlikePlant(this.props.plant.plantId);
    }
    render() {
        dayjs.extend(relativeTime)
        const { 
                classes, 
                plant : { 
                    caption, 
                    createDate, 
                    userImage, 
                    userHandle, 
                    plantId, 
                    plantName, 
                    plantImg, 
                    kingdom, 
                    phylum,
                    klass,
                    urder,
                    family,
                    genus,
                    species,
                    likeCount, 
                    commentCount 
                },
                user: {
                    authenticated, credentials: { handle } // TO DO replace with { handle, role }
                } 
            } = this.props;
        const likeButton = !authenticated ? (
            <MyButton tip="Like">
                <Link to="/login">
                    <FavoriteBorder color="primary"/>
                </Link>
            </MyButton>
        ) : (
            this.likedPlant() ? (
                <MyButton tip="Unlike" onClick={this.unlikePlant}>
                    <FavoriteIcon color="primary"/>
                </MyButton>
            ) : (
                <MyButton tip="Like" onClick={this.likePlant}>
                    <FavoriteBorder color="primary"/>
                </MyButton>
            )
        );
        const deleteButton = 
        authenticated && userHandle === handle ? ( // TODO include OR for role = moderator
            <DeletePlant plantId={plantId} />
        ) : null;
        return (
            <Card className={classes.card}>
                <CardContent className={classes.content}>
                    <Avatar alt={userHandle} src={userImage} />
                    {deleteButton}
                    <Typography
                         variant="body2" 
                         component={Link} 
                         to={`/users/${userHandle}`} 
                         color="primary">{userHandle}
                    </Typography>

                    <Typography variant="body2" color="textSecondary" component="p">{dayjs(createDate).fromNow()}</Typography>
                </CardContent>
                <CardMedia className={classes.media} image={plantImg} title={plantName} />

                <CardContent className={classes.content}>
                    {likeButton}
                    <span>{likeCount} Likes</span>
                    <MyButton tip="Comments">
                        <ChatIcon color="primary"/>
                    </MyButton>
                    <span>{commentCount} Comments</span>
                    <Typography variant="h5">{plantName}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Kingdom: </strong>{kingdom}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Phylum: </strong>{phylum}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Class: </strong>{klass}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Order: </strong> {urder}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Family: </strong>{family}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Genus: </strong>{genus}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Species: </strong>{species}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p"><strong>Description: </strong>{caption}</Typography>
                </CardContent>

            </Card>
        );
    }
}

Plant.propTypes = {
    likePlant: PropTypes.func.isRequired,
    unlikePlant: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
    plant: PropTypes.object.isRequired,
    classes: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    user: state.user
})

const mapActionsToProps = {
    likePlant,
    unlikePlant
}

export default connect(mapStateToProps, mapActionsToProps)(withStyles(styles)(Plant));
