import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import { Link } from 'react-router-dom';

// Material UIs
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { lightGreen } from '@material-ui/core/colors';

const styles = {
    card: {
        display: 'flex',
        marginBottom: 20,
    },
    image: {
        minWidth: 400,
        minHeight: 400,
    },
    content: {
        padding: 20,
        objectFit: 'cover',
        minWidth: 400,
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    avatar: {
        backgroundColor: lightGreen[500],
      },
}

class Plant extends Component {
    render() {
        const { 
                classes, 
                plant : { 
                    caption, 
                    createDate, 
                    userImage, 
                    userHandle, 
                    plantId, 
                    plantName, 
                    plantImg, 
                    kingdom, 
                    likeCount, 
                    commentCount 
                } 
            } = this.props
        return (
            <Card className={classes.card}>
                <CardMedia className={classes.media} image={plantImg} title="Plant image" className={classes.image}/>

                <CardContent className={classes.content}>
                    <Avatar alt={userHandle} src={userImage} />
                    <Typography
                         variant="body2" 
                         component={Link} 
                         to={`/users/${userHandle}`} 
                         color="primary">{userHandle}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p">{createDate}</Typography>
                    <Typography variant="h4">{plantName}</Typography>
                    <Typography variant="body2" color="textSecondary" component="p">{caption}</Typography>
                </CardContent>

            </Card>
        )
    }
}

export default withStyles(styles)(Plant);
